mapper.insert:
insert into freeboard ( id, title, writer, content
)values ( #{id}, #{title}, #{writer}, #{smarteditor}
) on duplicate key update id = #{id}, title = #{title}, writer = #{writer}, content = #{smarteditor}

insert into freeboardreply (
board_id, parent_id, depth, reply_content, reply_writer
)values (
#{board_id}, #{parent_id}, #{depth}, #{reply_content}, #{reply_writer}
) on duplicate key update
board_id = #{board_id}, parent_id = #{parent_id}, depth = #{depth}, reply_content = #{reply_content}, reply_writer = #{reply_writer}
